import React, { Component } from 'react'

export class NewsItem extends Component {

  render() {
    let { title, description, imageUrl, newsUrl, author, date, source } = this.props;
    return (
      <>
        <div className='my-3'>
          <div className="card" >
            <span class="position-absolute top-0  translate-middle badge rounded-pill bg-danger" style={{ left: '85%', zIndex: "1" }}> {source} </span>
            <img src={!imageUrl ? "https://c.ndtvimg.com/2022-08/9n5rlmn8_pakistan-floods-afp-pic_650x400_30_August_22.jpg" : imageUrl} className="card-img-top" alt="..." />
            <div className="card-body">
              <h5 className="card-title">{title}</h5>
              <p className="card-text">{description}</p>

              <p className='card-text'><small class="text-muted"> By {!author ? "unknown" : author} on {new Date(date).toGMTString()}</small></p>

              <a rel="noreferrer" href={newsUrl} target="_blank" className="btn btn-sm btn-dark">Read More</a>
            </div>
          </div>
        </div>
      </>
    )
  }
}

export default NewsItem;
