import React, { useEffect,useState} from 'react'
import NewsItem from './NewsItem';
import PropTypes from 'prop-types';
import InfiniteScroll from "react-infinite-scroll-component";

;


const News =(props)=> {
  const[articles, setArticles] =useState([]);
  const[loading, setloading] =useState(true);
  const[page, setpage] =useState(1);
  const[totalResults, setTotalResults] =useState(0);

 

  const updateNews =async ()=> {
    props.setProgress(10);
    let url = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apiKey=9912f2f63e8246fd9ce5dfeedb719192&page=${state.page}&pageSize=${props.pageSize}`;
    let data = await fetch(url);
    props.setProgress(30);
    let parsedData = await data.json();
    props.setProgress(70);
    setArticles(parsedData.articles)
    setTotalResults(parsedData.totalResults)
    setloading(false)
  
    props.setProgress(100);
  }

  useEffect (()=>{
    this.updateNews();

  },[])


  
  handlePreClick = async () => {
    this.setState({ page: this.state.page - 1 })


  }

  handleNxtClick = async () => {
    this.setState({ page: this.state.page + 1 })

  }

  fetchMoreData = async () => {
    this.setState({ page: this.state.page + 1 })
    let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=9912f2f63e8246fd9ce5dfeedb719192&page=${this.state.page}&pageSize=${this.props.pageSize}`;
    let data = await fetch(url);
    let parsedData = await data.json();
    this.setState({
      articles: parsedData.articles.concat(parsedData.articles),
      totalResults: parsedData.totalResults,
      loading: false
    });

  }


 

    return (

      <div className='container my-2'>
        <h2 className='text-center' style={{ margin: "35px 0px" }}>Top-Headlines</h2>


        <InfiniteScroll

          dataLength={this.state.articles.length}
          next={this.fetchMoreData}
          hasMore={this.state.articles.length !== this.state.totalResults}
          loader={<h4>Loading...</h4>}

        >
          <div className='container'>

            <div className='row'>
              {this.state.articles.map((element) => {
                return <div className='col-md-4' key={element.url}>
                  <NewsItem title={element.title ? element.title : ""} description={element.description ? element.description : ""} imageUrl={element.urlToImage} newsUrl={element.url} author={element.author} date={element.publishedAt} source={element.source.name} />

                </div>

              })}

            </div>
          </div>
        </InfiniteScroll>
      </div>
    )
  

}

News.defaultProps = {
  country: "in",
  pageSize: 5,
  category: "general"
}
News.propTypes = {
  country: PropTypes.string,
  pageSize: PropTypes.number,
  category: PropTypes.string,
}
export default News;
